import Header from "./components/Header";
import Footer from "./components/Footer";
import HomeScreen from "./Screens/HomeScreen";

import {Container} from 'react-bootstrap'
import { useSelector } from "react-redux";
import React from 'react'
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom' 
import ProductScreen from "./Screens/ProductScreen";
import LoginModal from "./components/LoginModalComponent";
import RegisterModalComponent from "./components/RegisterModalComponent"

function App() {
  const value = useSelector((state) => state.modal.value)
  const registerValue = useSelector((state) => state.registerModal.value)

  return (
    <Router>
      <div>
        {value && <LoginModal />}
        {registerValue && <RegisterModalComponent />}
        {value ? <LoginModal /> : <Header />}
        {/* <Header/> */}
        <main className="py-5">
          <Container>
            <Routes>
              <Route path="/eCommerceWebshop.github.io" element={<HomeScreen/>} exact/>
              <Route path="/eCommerceWebshop.github.io/product/:id" element={<ProductScreen/>} exact/>
            </Routes>
          </Container>
        </main>
        <Footer />
      </div>
    </Router>

  )
}

export default App
